       , #{payTime,jdbcType=TIMESTAMP}, #{orderStatus,jdbcType=TINYINT}, #{extraInfo,jdbcType=VARCHAR}
       , #{isDeleted,jdbcType=TINYINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP})

mapper.select:
select order_id
from tb_newbee_mall_order
where order_no = #{orderNo};

select tb_order.order_id, tb_order.create_time, tb_order.order_no, tb_order.order_status, tb_order.total_price, tb_order.pay_type, orderItem.order_item_id, orderItem.goods_count, orderItem.goods_cover_img, orderItem.goods_id, orderItem.goods_name, orderItem.selling_price from tb_newbee_mall_order tb_order, tb_newbee_mall_order_item orderItem
tb_order.is_deleted = 0
and tb_order.order_status = #{status}
and tb_order.order_id = orderItem.order_id
order by tb_order.order_id desc limit #{limit},#{pageSize}

